import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { BehaviorSubject } from "rxjs";
import { combineLatest } from "rxjs";
import { map } from "rxjs/operators";
let AppComponent = class AppComponent {
    constructor(todoService) {
        this.searchTermSubject = new BehaviorSubject(''); // Search term
        this.todos$ = todoService.getAll();
        // Combine todos$ with searchTermSubject to filter the list dynamically
        this.filteredTodos$ = combineLatest([this.todos$, this.searchTermSubject]).pipe(map(([todos, searchTerm]) => todos.filter(todo => todo.task.toLowerCase().includes(searchTerm.toLowerCase()))));
    }
    // Called when the search input changes
    onSearch(searchTerm) {
        this.searchTermSubject.next(searchTerm); // Update the search term
    }
};
AppComponent = __decorate([
    Component({
        selector: 'app-root',
        template: `
    <div class="title">
      <h1>
        A list of TODOs
      </h1>
    </div>
    <div class="list">
      <label for="search">Search...</label>
        <input id="search" type="text" (input)="onSearch($event.target ? ($event.target as HTMLInputElement).value : '')">
      <app-progress-bar></app-progress-bar>
      <app-todo-item *ngFor="let todo of filteredTodos$ | async" [item]="todo"></app-todo-item>
    </div>
  `,
        styleUrls: ['app.component.scss']
    })
], AppComponent);
export { AppComponent };
